import { Component, Input, OnInit } from '@angular/core';
import { CarouselImage } from 'src/assets/img/Data/carousel';


@Component({
  selector: 'app-main-carousel',
  templateUrl: './main-carousel.component.html',
  styleUrls: ['./main-carousel.component.scss'],
})
export class MainCarouselComponent implements OnInit {

  @Input() images: CarouselImage[] = [];
  @Input() indicators = true;
  @Input() controls=true; 
  @Input() autoSlide=false; 
  @Input() slideInterval=3000; 
  ngOnInit(): void {
    if (this.autoSlide) {
      this.autoSlideImages();
    }
  }
  autoSlideImages() {
    setInterval(() => {
      this.onNextClick();
    }, this.slideInterval);
  }

  constructor() {}

  selectedIndex = 0;
  selectImage(index: number) {
    this.selectedIndex=index;
  }
  onPrevClick(){
    if (this.selectedIndex===0) {
      this.selectedIndex=this.images.length-1
    }else{
      this.selectedIndex--;
    }
  }
  onNextClick(){
    if (this.selectedIndex===this.images.length-1) {
      this.selectedIndex=0
    }else{
      this.selectedIndex++;
    }
  }
}

<div *ngIf="images && images.length > 0" class="carousel-container">
  <div *ngFor="let image of images; let i = index">

    <img

      [src]="image.imageSrc"
      [alt]="image.imageAlt"
      [ngClass]="{ 'image-active': selectedIndex === i }"
      class="fade"

    >
    <div class="description" [ngClass]="{ active: selectedIndex === i }">
      <h1 class="description-item">{{image.paragraphDescription}}</h1>
      <h2 class="description-item">{{image.subDescription}}</h2>
      <h2 class="description-item">{{image.adv}}</h2>
    </div>
    <div *ngIf="indicators" class="carousel-dot-container">

      <span
        *ngFor="let dot of images; let i = index"
        class="dot"
        [ngClass]="{ active: selectedIndex === i }"
        (click)="selectImage(i)"

      >

      </span>
    </div>
    <button *ngIf="controls" class="btn-carousel btn-prev" (click)="onPrevClick()">
      <i class="fas fa-arrow-circle-left icon-carousel icon-prev"></i>
    </button>
    <button *ngIf="controls" class="btn-carousel btn-next" (click)="onNextClick()">
      <i class="fas fa-arrow-circle-right icon-carousel icon-next"></i>
    </button>
  </div>
