import { Component, OnInit } from '@angular/core';
import { Brand } from '../_model/brand-modal';
import { NgForm} from '@angular/forms';
import { BrandService } from '../_services/brand.service';
import { ProductService } from '../_services/product.service';
import { Category } from '../_model/category-modal';

@Component({
  selector: 'app-brand',
  templateUrl: './brand.component.html',
  styleUrls: ['./brand.component.scss']
})

export class BrandComponent implements OnInit{

  brand :Brand={
    id:0,
    brandName:'',
    categoryId:0
  };
  selectedOption: number = 0;
  categoryArr: Category[] = [];
  constructor(private brandService:BrandService,
    private productService:ProductService){}
  ngOnInit(): void {
    this.getAllCategory();

    }



  getAllCategory() {
    this.productService.getAllCategories().subscribe(
      (resp) => {
        console.log(resp);
        for (let i = 0; i < resp.length; i++) {
          this.categoryArr[i] = resp[i];
        }
      },
      (err) => {
        console.log(err);
      }
    );
  }
  addBrand(brandForm:NgForm){
  this.brand.categoryId=this.onCategorySelected();
console.log(this.brand.categoryId);

   this.productService.addNewBrand(this.brand).subscribe(resp=>{

  this.brand.brandName=resp.brandName; 
  brandForm.reset();

  })
  }
  onCategorySelected() {
    return this.selectedOption;
  }
}
<div class="container mt-5">
  <div class="card p-2" style="background-color: skyblue">
    <div class="pt-2 px-4 ps-lg-0 pe-xl-5">
      <!-- Title-->
      <div class="d-sm-flex flex-wrap justify-content-between align-items-center pb-2">
        <h2 class="h3 py-2 me-2 text-center text-sm-start">
          Add New Brand
        </h2>
      </div>
      <form #brandForm="ngForm" (ngSubmit)="addBrand(brandForm)">
        <div class="mb-3 pb-2">
          <label class="form-label" for="unp-product-name">Brand name</label>
          <input
            class="form-control"
            type="text"
            id="unp-product-name"
            [(ngModel)]="brand.brandName"
            name="brandName"
          />
          <div class="form-text">
            Maximum 100 characters. No HTML or emoji allowed.
          </div>
          <div class="py-2">
            <select class="form-select me-2" id="unp-category"  [(ngModel)]="selectedOption" [ngModelOptions]="{standalone: true}" (change)="onCategorySelected()">
              <option>Select category</option>
              <option *ngFor="let item of categoryArr" [value]="item.id">{{item.categoryName}}</option>
            </select>
          </div>
        </div>
        <button class="btn btn-primary d-block w-100" type="submit">
          <i class="ci-cloud-upload fs-lg me-2"></i>Upload Brand
        </button>
      </form>
    </div>
  </div>
</div>
