import { Component, NgModule, OnInit } from '@angular/core';
import { Product } from '../_model/product.model';
import { MyOrderDetails } from '../_model/order.model';
import { ProductService } from '../_services/product.service';
import { FormBuilder, FormGroup,FormsModule } from '@angular/forms';


@Component({
  selector: 'app-order-details',
  templateUrl: './order-details.component.html',
  styleUrls: ['./order-details.component.scss']
})

export class OrderDetailsComponent implements OnInit {
  status:string='All';

    ngOnInit(): void {
    this.getAllOrderDetailsForAdmin(this.status);
  }

  dataSource: MyOrderDetails[] = [];
  displayedColumns: string[] = ['Id', 'Product Name', 'Name', 'Address', 'Contact No', 'Status', 'Action','Add Days'];

  constructor(private productService: ProductService) { }
  increment(element: any) {

    element.addedDays++;

  }
  decrement(element: any) {

    if (element.addedDays > 0) {
      element.addedDays--;
    }
  }
  getAllOrderDetailsForAdmin(statusParameter:string) {
    this.productService.getAllOrderDetailsForAdmin(statusParameter).subscribe(
      (resp) => {
                this.dataSource = resp;
                this.dataSource.forEach((element) => {
                  element.addedDays =" 0";
                });
        console.log(resp);

      }, (err) => {
        console.log(err);

      }
    );
  }
  markAsDelivered(orderId: number,addedDays:string) {
    let addedDaysNumber: number = parseInt(addedDays);
    this.productService.markOrderAsDelivered(orderId,addedDaysNumber).subscribe(
      (resp)=>{
        this.getAllOrderDetailsForAdmin(this.status);
        console.log(resp);


      },(err)=>{
        console.log(err);

      }
    );
  }
  markAsProcessing(orderId: number,addedDays:string) {
    let addedDaysNumber: number = parseInt(addedDays);
    this.productService.markOrderAsProcessing(orderId,addedDaysNumber).subscribe(
      (resp)=>{
        this.getAllOrderDetailsForAdmin(this.status);
        console.log(resp);


      },(err)=>{
        console.log(err);

      }
    );
  }
  markAsQualityCheck(orderId: number,addedDays:string) {
    let addedDaysNumber: number = parseInt(addedDays);
    this.productService.markOrderAsQualityCheck(orderId,addedDaysNumber).subscribe(
      (resp)=>{
        this.getAllOrderDetailsForAdmin(this.status);
        console.log(resp);


      },(err)=>{
        console.log(err);

      }
    );
  }


}
<div class="container mt-5">
  <mat-button-toggle-group aria-label="Font Style">
    <mat-button-toggle value="All" (click)="getAllOrderDetailsForAdmin('All')"
      >All</mat-button-toggle
    >
    <mat-button-toggle
      value="Placed"
      (click)="getAllOrderDetailsForAdmin('Placed')"
      >Placed</mat-button-toggle
    >
    <mat-button-toggle
      value="Delivered"
      (click)="getAllOrderDetailsForAdmin('Processing')"
      >Processing</mat-button-toggle
    >
    <mat-button-toggle
      value="Delivered"
      (click)="getAllOrderDetailsForAdmin('Quality Check')"
      >Quality Check</mat-button-toggle
    >
    <mat-button-toggle
      value="Delivered"
      (click)="getAllOrderDetailsForAdmin('Delivered')"
      >Delivered</mat-button-toggle
    >
  </mat-button-toggle-group>
  <div class="card">
    <table mat-table [dataSource]="dataSource" class="mat-elevation-z8 mt-3">
      <ng-container matColumnDef="Id">
        <th mat-header-cell *matHeaderCellDef>Id</th>
        <td mat-cell *matCellDef="let element">{{ element.orderId }}</td>
      </ng-container>
      <ng-container matColumnDef="Product Name">
        <th mat-header-cell *matHeaderCellDef>Product Name</th>
        <td mat-cell *matCellDef="let element">{{ element.orderFullName }}</td>
      </ng-container>
      <ng-container matColumnDef="Name">
        <th mat-header-cell *matHeaderCellDef>Name</th>
        <td mat-cell *matCellDef="let element">{{ element.orderFullName }}</td>
      </ng-container>
      <ng-container matColumnDef="Address">
        <th mat-header-cell *matHeaderCellDef>Address</th>
        <td mat-cell *matCellDef="let element">
          {{ element.orderFullAddress }}
        </td>
      </ng-container>
      <ng-container matColumnDef="Contact No">
        <th mat-header-cell *matHeaderCellDef>Contact No</th>
        <td mat-cell *matCellDef="let element">
          {{ element.orderContactNumber }}
        </td>
      </ng-container>
      <ng-container matColumnDef="Status">
        <th mat-header-cell *matHeaderCellDef>Status</th>
        <td mat-cell *matCellDef="let element">{{ element.orderStatus }}</td>
      </ng-container>
      <ng-container matColumnDef="Action">
        <th mat-header-cell *matHeaderCellDef>Action</th>
        <td mat-cell *matCellDef="let element">
          <button
            type="button"
            mat-raised-button
            color="primary"
            (click)="markAsProcessing(element.orderId,element.addedDays)"
            [disabled]="element.orderStatus !== 'Placed'"
          >
            Processing
          </button>
          <button
            type="button"
            mat-raised-button
            color="primary"
            (click)="markAsQualityCheck(element.orderId,element.addedDays)"
            [disabled]="element.orderStatus !== 'Processing'"
          >
            QualityCheck
          </button>
          <button
            type="button"
            mat-raised-button
            color="primary"
            (click)="markAsDelivered(element.orderId, element.addedDays)"
            [disabled]="element.orderStatus !== 'Quality Check'"
          >
            Delivered
          </button>
        </td>
      </ng-container>

      <ng-container matColumnDef="Add Days">
        <th mat-header-cell *matHeaderCellDef>Add Days</th>
        <td mat-cell *matCellDef="let element" class="add-days-cell">
          <input
            type="text"
            [(ngModel)]="element.addedDays"
            class="add-days-input"
            value="0"
          />
