import { Component, OnInit } from '@angular/core';
import { ProductService } from '../_services/product.service';
import { Product } from '../_model/product.model';
import { HttpErrorResponse } from '@angular/common/http';
import { MatDialog } from '@angular/material/dialog';
import { ShowProductImagesDialogComponent } from '../show-product-images-dialog/show-product-images-dialog.component';
import { ImageProcessingService } from '../image-processing.service';
import { map } from 'rxjs';
import { Router } from '@angular/router';
import { HeaderService } from '../_services/header.service';

@Component({
  selector: 'app-show-product-details',
  templateUrl: './show-product-details.component.html',
  styleUrls: ['./show-product-details.component.scss'],
})
export class ShowProductDetailsComponent implements OnInit {

  searchkeyword: string = '';
  productDetails: Product[] = [];
  displayedColumns: string[] = [
    'Id',
    'Product Name',
    'description',
    'Product Discounted Price',
    'Product Actual Price',
    'Actions',
  ];
  showLoadMoreProduct = false;
  showTable = false;
  pageNumber: number = 0;
  ngOnInit(): void {

    this.getAllProducts();
    this.searchByKeyword();
  }

  constructor(
    private productService: ProductService,
    private imagesDialog: MatDialog,
    private imageProcessingService: ImageProcessingService,
    private router: Router,
    private headerService: HeaderService,

  ) {}

  public getAllProducts() {
    this.showTable = false;
    this.productService
      .getProducts(this.pageNumber, this.searchkeyword)
      .pipe(
        map((x: Product[], i) =>
          x.map((product: Product) =>
            this.imageProcessingService.createImages(product)
          )
        )
      )
      .subscribe(
        (resp: Product[]) => {
          resp.forEach((p) => this.productDetails.push(p));
          this.showTable = true;
          console.log(this.productDetails);
          if (resp.length == 4) {
            this.showLoadMoreProduct = true;
          } else {
            this.showLoadMoreProduct = false;
          }
        },
        (err: HttpErrorResponse) => {
          console.log(err);
        }
      );
  }
  deleteProduct(productId: any) {
    this.productService.deleteProduct(productId).subscribe(
      (resp) => {
        this.getAllProducts();
      },
      (err: HttpErrorResponse) => {
        console.log(err);
      }
    );
  }
  showImages(product: Product) {
    console.log(product);
    this.imagesDialog.open(ShowProductImagesDialogComponent, {
      data: {
        images: product.productImages,
      },
      height: '500px',
      width: '800px',
    });
  }
  editProductDetails(productId: number) {
    this.router.navigate(['/addNewProduct', { productId: productId }]);
  }
  loadMoreProduct() {
    this.pageNumber = this.pageNumber + 1;
    this.getAllProducts();
  }
  searchByKeyword() {
    this.headerService.search.subscribe((resp) => {
      this.searchkeyword = resp;
      console.log(this.searchkeyword);
      this.pageNumber = 0;
      this.productDetails = [];
      this.getAllProducts();
    });
  }

  }

<div class="mt-5 p-3">

  <div *ngIf="showTable">
    <table mat-table [dataSource]="productDetails" class="mat-elevation-z8">
        <ng-container matColumnDef="Id">
          <th mat-header-cell *matHeaderCellDef>Id </th>
          <td mat-cell *matCellDef="let element"> {{element.productId}} </td>
        </ng-container>

        <ng-container matColumnDef="Product Name">
          <th mat-header-cell *matHeaderCellDef> Product Name </th>
          <td mat-cell *matCellDef="let element"> {{element.productName}} </td>
        </ng-container>

        <ng-container matColumnDef="description">
          <th mat-header-cell *matHeaderCellDef> Product Description </th>
          <td mat-cell *matCellDef="let element"> {{element.productDescription}} </td>
        </ng-container>

        <ng-container matColumnDef="Product Discounted Price">
          <th mat-header-cell *matHeaderCellDef> Product Discounted Price </th>
          <td mat-cell *matCellDef="let element"> {{element.productDiscountedPrice}} </td>
        </ng-container>
        <ng-container matColumnDef="Product Actual Price">
            <th mat-header-cell *matHeaderCellDef> Product Actual Price </th>
            <td mat-cell *matCellDef="let element"> {{element.productActualPrice}} </td>
        </ng-container>
        <ng-container matColumnDef="Actions">
          <th mat-header-cell *matHeaderCellDef> Actions </th>
          <td mat-cell *matCellDef="let element"> 
            <button mat-icon-button color="primary"  (click)="showImages(element)">
              <mat-icon>images</mat-icon>
            </button>
            <button mat-icon-button color="primary">
              <mat-icon>edit</mat-icon>
            </button>
            <button mat-icon-button color="warn" (click)="deleteProduct(element.productId)">
              <mat-icon>delete</mat-icon>
            </button> 
           </td>
      </ng-container>
        <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
        <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
      </table>
    </div>
    <div class="text-center p-5">
      <button *ngIf="showLoadMoreProduct" type="button" mat-raised-button color="primary" (click)="loadMoreProduct()">Load More</button>
    </div>


</div>
